import { Component } from '@angular/core';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.scss']
})

export class CardComponent {
        tipo = 'Teste';
        preco = 1000;

        getFullPrice() {
            setTimeout(() => {
              console.log('Set Timeout');

              this.tipo = 'Simples';
            }, 4000);

            return 'R$'+ this.preco + ',00/Mês';
        }
}
