import { Component } from '@angular/core';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.scss']
})

export class CardComponent {
    //

    //@ts-ignore
    plano;

  }
<div class="card">
    <div class="card__plan card__item">Plano <b>{{ undefined ? plano.tipo : 'vazio' }}</b></div>
    <div class="card__price card__item">R${{ plano?.preco }},00/Mes</div>
    <app-card-button></app-card-button>
</div>
