import { Component } from '@angular/core';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.scss']
})
export class CardComponent {

    tipo = 'Teste';
    preco = 1000;

    getFullPrice() {
      setTimeout(() => {
          console.log('Set Timeout');

          this.tipo = 'Simples';
      }, 4000)
      return 'R$' + this.preco + ',00/Mês'
    }

}
<div class="card">
    <div class="card__plan card__item">Plano <b>{{ tipo }}</b></div>
    <div class="card__price card__item">{{ getFullPrice() }}</div>
    <app-card-button></app-card-button>
</div>
